import {useState} from "react";
import useEth from "../../contexts/EthContext/useEth";
import './Voters.css'
import StyledButton from "../Buttons/StyledButton";

function Voters() {
    const { state: { contract, accounts } } = useEth();
    const [inputValue,setInputValue] = useState(null);

    const handleInputGetVoterChange = e => {
        if (/^\d+$|^$/.test(e.target.value)) {
            setInputValue(e.target.value);
        }
    };

    const getVoter = async () => {
        const value = await contract.methods.read().call({ from: accounts[0] });
        setValue(value);
    };


    return (
        <>
            <h2>les électeurs</h2>
            <p>Nous sommes transparents. A tout moment vous pouvez visualisez le vote d'un autre électeur. Il vous faudra l'adresse publique
            du wallet.</p>
            <div onClick={getVoter} className="input-btn">
                <input
                type="text"
                placeholder="uint"
                value={inputValue}
                onChange={handleInputGetVoterChange}
            />
            <StyledButton text="Visualiser un électeur" />
            </div>
        </>
    )
}

export default Voters;
