import { useState } from "react";
import useEth from "../../contexts/EthContext/useEth";
import './Steps.css'

function Steps() {
    const { state } = useEth();
    let [value, setValue, contractAddress] = useState("?");

    const read = async () => {
        const networkID = await state.networkID;
        contractAddress = networkID;
    };

    read();

    return (
        <>
            <h2>Le déroulement du vote</h2>
            <p>Adresse du contrat: {contractAddress}</p>
            <ul>
                <li>Etape précédente:</li>
                <li>Etape actuelle:</li>
                <li>Etape suivante:</li>
            </ul>

        </>
    )
}

export default Steps;
