import {useState} from "react";
import useEth from "../../contexts/EthContext/useEth";
import './Voters.css'
import StyledButton from "../Buttons/StyledButton";

function Voters() {
    const { state: { contract, accounts } } = useEth();
    const [inputValue,setInputValue] = useState('');

    const handleInputGetVoterChange = e => {
        setInputValue(e.target.value);
    };

    const getVoter = async () => {
        console.log(inputValue);
        const value = await contract.methods.getVoter(inputValue).call({ from: accounts[0] });
        setInputValue(value);
    };


    return (
        <>
            <h2>les électeurs</h2>
            <p>Nous sommes transparents. A tout moment vous pouvez visualisez le vote d'un autre électeur. Il vous faudra l'adresse publique
            du wallet.</p>
            <div className="input-btn">
                <input
                type="text"
                placeholder="uint"
                value={inputValue}
                onChange={handleInputGetVoterChange}
            />
            <StyledButton click={getVoter} text="Visualiser un électeur" />
            </div>
        </>
    )
}

export default Voters;
