import {useEffect, useState} from "react";
import useEth from "../../contexts/EthContext/useEth";
import './Steps.css'

function Steps() {
    const { state } = useEth();
    const [contractAddress,setContractAddress] = useState(null);
    let networkID;

    const read = async () => {
        try{
            networkID =  state.networkID;
            let contractAddress =  await state.artifact.networks[networkID].address;
            setContractAddress(contractAddress);
        }catch(error){
             console.info("en attente du contrat");
        }
    };

    read();

    // useEffect(() => {
    //     const el = document.getElementById('address-container');
    //     console.log(contractAddress);
    //     el.innerText = contractAddress;
    // }, [contractAddress])

    return (
        <>
            <h2>Le déroulement du vote</h2>
            <p>Adresse du contrat: <span id="address-container">{ contractAddress }</span> </p>
            <p>
                Le propriétaire du contrat ouvrira la session des propositions après avoir ajouté les électeurs.
                Une fois la sessions des propositions closes, l'ouverture de la session de vote aura lieu.
                la proposition ayant récolté le plus de votes sera retenue.
            </p>
            <ul>
                <li>Etape précédente:</li>
                <li>Etape actuelle:</li>
                <li>Etape suivante:</li>
            </ul>

        </>
    )
}

export default Steps;
