import { useState } from "react";
import useEth from "../../contexts/EthContext/useEth";
import './Steps.css'

function Steps() {
    const { state } = useEth();
    let networkID;
    let contractAddress;

    const read = async () => {
        networkID =  state.networkID;
        contractAddress =  state.artifact.networks[networkID].address;
        await console.log(contractAddress)
    };

    read();

    return (
        <>
            <h2>Le déroulement du vote</h2>
            <p>Adresse du contrat: { contractAddress } </p>
            <ul>
                <li>Etape précédente:</li>
                <li>Etape actuelle:</li>
                <li>Etape suivante:</li>
            </ul>

        </>
    )
}

export default Steps;
