import { useState } from "react";
import useEth from "../../contexts/EthContext/useEth";
import { EthProvider } from "./contexts/EthContext";
import Footer from "./components/Footer";
import "./App.css";
import Presentation from "./components/Intro/Presentation";
import Banner from "./components/Intro/Banner";
import Steps from "./components/Steps/Steps";
import Voters from "./components/Voters/Voters";
import NoticeNoArtifact from "./components/Demo/NoticeNoArtifact";
import NoticeWrongNetwork from "./components/Demo/NoticeWrongNetwork";


function Content(){
  const { state } = useEth();

  <>
    <Presentation />
    <br/>
    <Steps />
    <br/>
    <Voters />
    <br/>
  </>;

}


function App() {
  return (
    <EthProvider>
      <div id="App" >
        <div className="container">
          <Banner />
          <br/>
          {
            !state.artifact ? <NoticeNoArtifact /> :
                !state.contract ? <NoticeWrongNetwork /> :
                    content
          }
          <Footer />
        </div>
      </div>
    </EthProvider>
  );
}

export default App;
